import React from 'react';
import Menu from '../Assets/menu.png';
import '../App.css'

class Header extends React.Component {
  constructor(props){
    super(props);
    this.state = {
        visibility: false
    };
    this.toggleNavBar = this.toggleNavBar.bind(this);
  }

  toggleNavBar(){
      this.setState(state => ({
         visibility: !state.visibility 
      }))
  }

  render () {
    let navBar = (
      <div className="d-flex flex-column flex-sm-row align-items-center" style={{color:'#EEEEEE'}}>
        <a className="m-2" href="./components/Me" style={{textDecoration:'none', color:'#EEEEEE'}}>Me</a>
        <a className="m-2" href="./components/Work" style={{textDecoration:'none', color:'#EEEEEE'}}>Work</a>
        <a className="m-2" href=".components/Stack" style={{textDecoration:'none', color:'#EEEEEE'}}>Stack</a>
        <a className="m-2" href=".components/Projects" style={{textDecoration:'none', color:'#EEEEEE'}}>Projects</a>
        <a className="m-2" href=".components/Contact" style={{textDecoration:'none', color:'#EEEEEE'}}>Contact</a>
        <a href="https://www.frontendmentor.io/profile/CodeEvolver"><button type="button" className="btn" style={{color: '#00ADB5', textDecoration:'none', border:'2px solid #00ADB5', width: 'fit-content'}}>Hire Me</button></a>
      </div>
          );
    let none = ("");
    return (
    <div className="surface1-dark w-100 p-3 px-sm-5 sticky-top d-flex flex-column flex-sm-row justify-content-sm-center align-items-center text1-dark">
      <div className="d-flex flex-row w-100 align-items-center justify-content-between">
        <h1 className="logo col-sm-auto align-middle">Goody</h1>
        <button className="bg-transparent border-0 d-sm-none" style={{width: 'fit-content'}} onClick={this.toggleNavBar}><img src={Menu}alt="menu" style={{width:25, height: 'auto'}}/></button>
        <span className="d-none d-sm-block align-middle">{navBar}</span>
      </div>
      <span className="d-sm-none position-absolute  my-5 w-100 surface2-dark">{this.state.visibility? navBar: none}</span>
    </div>
    );
  }
}

export { Header }
