import React from 'react';
import DevImg from '../Assets/developer-boy.png';

class Lead extends React.Component {
    constructor(props){
        super(props);
    }

    render () {
        return (
        <div className="w-100 p-3 p-sm-5 d-flex flex-column flex-sm-row-reverse justify-content-center align-items-sm-center" style={{backgroundColor: '#222831'}}>
            <img src={DevImg} alt="young developer image" className="w-100" />
            <div className="w-100 text-sm-start" style={{}}>
                <h2 style={{opacity: '0.9', fontSize: 'clamp(16px,25px,30px)', color: '#EEEEEE'}}>Hello, Universe!</h2>
                <h1 style={{fontSize: 'clamp(18px,30px,40px)', color:'#EEEEEE'}}>I'm Goodness, a <span style={{textShadow:'0px 0px 3px #00ADB5', fontWeight:'bold'}}>Frontend Developer</span></h1>
                <p style={{color: '#EEEEEE', fontSize: 'clamp(10px, 14px, 20px)'}}>I build stunning and business evolving websites</p>
                <button className="btn rounded-2 p-2 mb-5" style={{backgroundColor:'#00ADB5', color:'#FFFFFF',}}>Get in Touch</button>
            </div>
         </div>
        );
    }
}

export { Lead }
